sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var request = g_form.getValue('u_request_type');

	if(request == 'hr'){
		g_form.clearOptions('u_what_needed');
		g_form.setValue('u_what_needed', 'Human Resources 1');
		g_form.setValue('u_what_needed', 'Human Resources 2');
		g_form.setValue('u_what_needed', 'Others');
	}else if(request == 'facilities'){
		g_form.clearOptions('u_what_needed');
		g_form.setValue('u_what_needed', 'Facilities 1');
		g_form.setValue('u_what_needed', 'Facilities  2');
		g_form.setValue('u_what_needed', 'Others');
	}else if(request == 'legal'){
		g_form.clearOptions('u_what_needed');
		g_form.setValue('u_what_needed', 'Legal 1');
		g_form.setValue('u_what_needed', 'Legal  2');
		g_form.setValue('u_what_needed', 'Others');
	}

}
